        super()
        this.state = {
            
        }
        

    }
    notification = ()=>{
        if(localStorage.getItem("error").length>3){
            return   <Alert key={'error'} variant={'danger'}>
                {localStorage.getItem('error')}
            </Alert>

        } if(localStorage.getItem('success').length>3){
            return   <Alert key={'success'} variant={'success'}>
                {localStorage.getItem('success')}
            </Alert>
        }

    }

    render () {
        return (
            <div>{this.notification()}Accueil </div>

                  )
    }



    
}
