import React from 'react';
import {Link} from 'react-router-dom';
import image from '../../public/user.jpeg'

export default function Message({message}) {
    const {auteur,msg} = message;
    return (
        <>
         <article>
             <div>
             <img src={image} alt="img"/>
             <h3>{auteur}</h3>
             </div>
            <p>{msg}</p>
        </article>
        
        </>  
    )
}
