import React from 'react';
import CanvasJSReact from'./canvasjs.react';
import API from './API';
let CanvasJS = CanvasJSReact.CanvasJS;
let CanvasJSChart = CanvasJSReact.CanvasJSChart;

let historiqueNet =[];
export default class myDash extends React.Component{

    constructor(props) {
        super(props);
        this.getHisto ();
      /*  this.state = {
            data1: [],
            data2: [],
        }
*/


    }

    async getHisto (){
  /*      let historiqueBrut =   await API.getAllHistorique();

        await  historiqueBrut.data.filter((t)=>{
            if(t['user']==API.getUser()){historiqueNet.push({x:  new Date(t['date']),y: t['score']*1}) };
        })
        this.setState({ data1 : historiqueNet,

        });*/
    }

    render() {



        let options = {
            animationEnabled: true,
            exportEnabled: true,
            theme: "light1", // "light1", "dark1", "dark2"
            title:{
                text: "Trip Expenses"
            },
            data: [{
                type: "pie",
                indexLabel: "{label}: {y}%",
                startAngle: -90,
                dataPoints: [
                    { y: 20, label: "Airfare" },
                    { y: 24, label: "Food & Drinks" },
                    { y: 20, label: "Accomodation" },
                    { y: 14, label: "Transportation" },
                    { y: 12, label: "Activities" },
                    { y: 10, label: "Misc" }
                ]
            }]}


        let options2 = {
            animationEnabled: true,
            theme: "light2",
            title:{
                text: "Evolution du score du joueur"
            },
            axisX:{
                valueFormatString: "DD MM YY / HH:mm:ss",
                crosshair: {
                    enabled: true,
                    snapToDataPoint: true
                }
            },
            axisY: {
                title: "Score (in EUR)",
                includeZero: false,
                valueFormatString: "€##0.00",
                crosshair: {
                    enabled: true,
                    snapToDataPoint: true,
                    labelFormatter: function(e) {
                        return "€" + CanvasJS.formatNumber(e.value, "##0.00");
                    }
                }
            },
            data: [{
                type: "area",
                xValueFormatString: "DD MMM",
                yValueFormatString: "€##0.00",
                dataPoints:this.state.data1,
            }]

        }

        return (
            <div className="stat">


                <div  className="interface">
                    <div className="container">
                        <div>
                            <CanvasJSChart options = {options2}
                                /* onRef={ref => this.chart = ref} */
                            />
                            {/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
                        </div>
                        <div>
                            <CanvasJSChart options = {options}
                                           onRef={ref => this.chart = ref}
                            />
                            {/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
                        </div>

                    </div>
                </div>

            </div>

        )
    }
}
